      function poip_settings_toggle(elem) {
        $(elem).toggle();
        $(elem).siblings('#poip_settings_values').toggle();
        $(elem).siblings('#poip_settings_on').toggle();
        $(elem).siblings('#poip_settings_off').toggle();
